      alignItems: 'center',
      background: 'none',
      border: 'none',
      borderRadius: '4px',
      padding: '6px 8px',
      margin: '0 2px',
      fontSize: '13px',
      cursor: 'pointer',
      color: '#4c7977',
      transition: 'all 0.2s ease',
      position: 'relative',
      '&:hover': {
        backgroundColor: 'rgba(43, 87, 154, 0.1)'
      }
    },
    caret: {
      marginLeft: '2px',
