import React from 'react';
import { colors, spacing, typography, shadows, borderRadius } from '../../styles/theme';

const ImportExportActions = ({ onExport, onImport }) => {
  const styles = {
    container: {
      backgroundColor: colors.white,
      borderRadius: borderRadius.lg,
      padding: spacing.lg,
      boxShadow: shadows.sm,
      marginBottom: spacing.lg,
      display: "flex",
      justifyContent: "space-between",
      alignItems: "center",
      flexWrap: "wrap",
      gap: spacing.md
    },
    actionColumn: {
      display: "flex",
      flexDirection: "column",
      alignItems: "flex-start"
    },
    actionColumnEnd: {
      display: "flex",
      flexDirection: "column",
      alignItems: "flex-end"
    },
    button: {
      padding: `${spacing.sm} ${spacing.lg}`,
      backgroundColor: colors.white,
      color: colors.primary,
      border: `1px solid ${colors.primary}`,
      borderRadius: borderRadius.md,
      fontSize: typography.fontSize.sm,
      fontWeight: typography.fontWeight.medium,
      cursor: "pointer",
      display: "flex",
      alignItems: "center",
      gap: spacing.sm,
      transition: "all 0.3s ease"
    },
    helperText: {
      fontSize: typography.fontSize.xs,
      color: colors.textSecondary,
      marginTop: spacing.xs
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.actionColumn}>
        <label 
          htmlFor="importFile" 
          style={styles.button}
          onMouseEnter={(e) => {
            e.currentTarget.style.transform = 'translateY(-2px)';
            e.currentTarget.style.boxShadow = shadows.md;
          }}
          onMouseLeave={(e) => {
            e.currentTarget.style.transform = 'translateY(0)';
            e.currentTarget.style.boxShadow = 'none';
          }}
        >
          <span>ðŸ“¤</span> Importar HTML
        </label>
        <input
          id="importFile"
          type="file"
          accept=".html,.htm"
          style={{ display: 'none' }}
          onChange={onImport}
        />
        <p style={styles.helperText}>Importar desde archivo</p>
      </div>
      
      <div style={styles.actionColumnEnd}>
        <button 
          onClick={onExport}
          style={styles.button}
          onMouseEnter={(e) => {
            e.currentTarget.style.transform = 'translateY(-2px)';
            e.currentTarget.style.boxShadow = shadows.md;
          }}
          onMouseLeave={(e) => {
            e.currentTarget.style.transform = 'translateY(0)';
            e.currentTarget.style.boxShadow = 'none';
          }}
        >
          <span>ðŸ“¥</span> Exportar HTML
        </button>
        <p style={styles.helperText}>Descargar como archivo</p>
      </div>
    </div>
  );
};

export default ImportExportActions;
