import React, { useState, useEffect } from "react";

const Header = () => {
  const [scrolled, setScrolled] = useState(false);
  const [menuOpen, setMenuOpen] = useState(false);
  
  // Efecto para detectar el scroll
  useEffect(() => {
    const handleScroll = () => {
      const isScrolled = window.scrollY > 10;
      if (isScrolled !== scrolled) {
        setScrolled(isScrolled);
      }
    };

    document.addEventListener('scroll', handleScroll);
    
    // Agregar estilos y fuentes
    const styleEl = document.createElement('style');
    styleEl.innerHTML = `
      @import url('https://fonts.googleapis.com/css2?family=Poppins:wght@300;400;600;700&family=Montserrat:wght@700;800&display=swap');
      
      @keyframes gradientBg {
        0% { background-position: 0% 50%; }
        50% { background-position: 100% 50%; }
        100% { background-position: 0% 50%; }
      }
      
      @keyframes pulse {
        0% { transform: scale(1); }
        50% { transform: scale(1.05); }
        100% { transform: scale(1); }
      }
      
      @keyframes fadeInDown {
        from { 
          opacity: 0;
          transform: translateY(-20px);
        }
        to { 
          opacity: 1;
          transform: translateY(0);
        }
      }
      
      @keyframes slideIn {
        from {
          transform: translateX(-30px);
          opacity: 0;
        }
        to {
          transform: translateX(0);
          opacity: 1;
        }
      }
    `;
    document.head.appendChild(styleEl);
    
    return () => {
      document.removeEventListener('scroll', handleScroll);
      document.head.removeChild(styleEl);
    };
  }, [scrolled]);

  // Manejar el toggle del menú móvil
  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <header 
      className={`fixed w-full z-50 transition-all duration-500 ${
        scrolled ? "py-2 shadow-lg bg-white" : "py-4 bg-transparent"
      }`}
      style={{
        fontFamily: "'Poppins', sans-serif"
      }}
    >
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center">
          {/* Logo y nombre */}
          <div 
            className="flex items-center"
            style={{
              animation: 'fadeInDown 0.8s ease-out'
            }}
          >
            <div 
              className="flex items-center justify-center w-12 h-12 rounded-full mr-3"
              style={{
                background: 'linear-gradient(135deg, #6366F1, #EC4899, #3B82F6)',
                backgroundSize: '200% 200%',
                animation: 'gradientBg 3s ease infinite, pulse 2s infinite'
              }}
            >
              <span className="text-white text-2xl font-bold">E</span>
            </div>
            <h1 
              className={`font-bold text-2xl transition-colors duration-300 ${
                scrolled ? "text-indigo-700" : "text-white"
              }`}
              style={{
                fontFamily: "'Montserrat', sans-serif",
                textShadow: scrolled ? 'none' : '2px 2px 4px rgba(0,0,0,0.3)',
                letterSpacing: '0.5px'
              }}
            >
              EducStation
            </h1>
          </div>
          
          {/* Navegación - Desktop */}
          <nav className="hidden md:flex space-x-8">
            {['Inicio', 'Noticias', 'Técnicas', 'Herramientas', 'Comunidad', 'Contacto'].map((item, index) => (
              <a 
                key={item} 
                href={`#${item.toLowerCase()}`}
                className={`font-medium transition-all duration-300 hover:text-indigo-500 ${
                  scrolled ? "text-gray-700" : "text-white"
                }`}
                style={{
                  animation: `fadeInDown ${0.3 + index * 0.1}s ease-out`,
                  textShadow: scrolled ? 'none' : '1px 1px 2px rgba(0,0,0,0.2)'
                }}
              >
                {item}
              </a>
            ))}
          </nav>
          
          {/* Botón de inicio de sesión - Desktop */}
          <div className="hidden md:block">
            <button 
              className="px-6 py-2 rounded-full text-white font-semibold transition-all duration-300 hover:shadow-lg hover:transform hover:scale-105"
              style={{
                background: 'linear-gradient(90deg, #4F46E5 0%, #7C3AED 100%)',
                animation: 'fadeInDown 0.9s ease-out',
                boxShadow: '0 4px 15px rgba(79, 70, 229, 0.4)'
              }}
            >
              Iniciar Sesión
            </button>
          </div>
          
          {/* Botón de menú - Mobile */}
          <button 
            className="md:hidden text-gray-500 focus:outline-none"
            onClick={toggleMenu}
          >
            <svg 
              className="h-6 w-6" 
              fill="none" 
              viewBox="0 0 24 24" 
              stroke="currentColor"
              style={{
                color: scrolled ? '#4F46E5' : 'white',
                filter: scrolled ? 'none' : 'drop-shadow(1px 1px 2px rgba(0,0,0,0.3))'
              }}
            >
              {menuOpen ? (
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              ) : (
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
              )}
            </svg>
          </button>
        </div>
        
        {/* Menú móvil */}
        {menuOpen && (
          <div 
            className="md:hidden mt-2 py-3 px-4 rounded-lg shadow-lg"
            style={{
              background: 'rgba(255, 255, 255, 0.95)',
              backdropFilter: 'blur(8px)',
              animation: 'fadeInDown 0.3s ease-out'
            }}
          >
            <nav className="flex flex-col space-y-3">
              {['Inicio', 'Noticias', 'Técnicas', 'Herramientas', 'Comunidad', 'Contacto'].map((item, index) => (
                <a 
                  key={item} 
                  href={`#${item.toLowerCase()}`}
                  className="font-medium text-gray-700 hover:text-indigo-500 transition-colors duration-200"
                  style={{
                    animation: `slideIn ${0.2 + index * 0.05}s ease-out`
                  }}
                >
                  {item}
                </a>
              ))}
              <button 
                className="mt-2 w-full px-4 py-2 rounded-full text-white font-semibold text-sm"
                style={{
                  background: 'linear-gradient(90deg, #4F46E5 0%, #7C3AED 100%)',
                  animation: 'slideIn 0.5s ease-out',
                  boxShadow: '0 4px 15px rgba(79, 70, 229, 0.3)'
                }}
              >
                Iniciar Sesión
              </button>
            </nav>
          </div>
        )}
      </div>
      
      {/* Degradado de fondo para cuando no hay scroll */}
      {!scrolled && (
        <div 
          className="absolute inset-0 -z-10"
          style={{
            background: 'linear-gradient(135deg, rgba(99, 102, 241, 0.9), rgba(236, 72, 153, 0.8), rgba(59, 130, 246, 0.85))',
            backgroundSize: '200% 200%',
            animation: 'gradientBg 10s ease infinite',
            borderBottom: '1px solid rgba(255, 255, 255, 0.2)'
          }}
        ></div>
      )}
    </header>
  );
};

export default Header;
