// src/components/admin/SyntaxHighlighter.jsx
import React, { useEffect, useRef } from 'react';

const SyntaxHighlighter = ({ content, onChange, textAreaRef }) => {
  const highlighterRef = useRef(null);
  const containerRef = useRef(null);
  
  // Sincronizar scroll entre el textArea y el resaltador
  const syncScroll = () => {
    if (highlighterRef.current && textAreaRef.current) {
      highlighterRef.current.scrollTop = textAreaRef.current.scrollTop;
      highlighterRef.current.scrollLeft = textAreaRef.current.scrollLeft;
    }
  };

  // Actualizar el contenido resaltado cuando cambia el contenido
  useEffect(() => {
    if (highlighterRef.current && textAreaRef.current) {
      try {
        // Aplicar resaltado usando highlight.js a través de una función simple
        updateHighlighting();
        
        // Configurar event listeners
        textAreaRef.current.addEventListener('scroll', syncScroll);
        
      } catch (error) {
        console.error("Error applying syntax highlighting:", error);
      }
      
      // Limpiar event listeners
      return () => {
        if (textAreaRef.current) {
          textAreaRef.current.removeEventListener('scroll', syncScroll);
        }
      };
    }
  }, [content]);
  
  // Cargar highlight.js dinámicamente y aplicar resaltado
  const updateHighlighting = () => {
    if (!highlighterRef.current) return;
    
    // Simplemente escapar HTML - para mantener exactamente el mismo formato
    const escapedContent = escapeHtml(content);
    
    // Envolvemos el código en pre>code para que highlight.js lo procese
    // pero asegurándonos de que esté en líneas separadas para mantener la sincronización
    const codeWithLines = escapedContent
      .split('\n')
      .map((line, i) => `<div class="code-line" data-line="${i+1}">${line || ' '}</div>`)
      .join('');
      
    highlighterRef.current.innerHTML = `<pre class="hljs language-html"><code>${codeWithLines}</code></pre>`;
    
    // Aplicar highlight.js si está disponible
    if (window.hljs) {
      const codeBlocks = highlighterRef.current.querySelectorAll('pre code');
      codeBlocks.forEach(block => {
        window.hljs.highlightElement(block);
      });
    } else {
      // Cargar highlight.js si no está disponible
      loadHighlightJs();
    }
  };
  
  // Cargar highlight.js dinámicamente
  const loadHighlightJs = () => {
    // Evitar cargar varias veces
    if (document.querySelector('script[src*="highlight.min.js"]')) return;
    
    // Cargar el CSS de highlight.js (tema monokai)
    const link = document.createElement('link');
    link.rel = 'stylesheet';
    link.href = 'https://cdnjs.cloudflare.com/ajax/libs/highlight.js/11.7.0/styles/monokai.min.css';
    document.head.appendChild(link);
    
    // Cargar el JavaScript de highlight.js
    const script = document.createElement('script');
    script.src = 'https://cdnjs.cloudflare.com/ajax/libs/highlight.js/11.7.0/highlight.min.js';
    script.onload = () => {
      // Una vez cargado, inicializarlo y aplicar a nuestro código
      updateHighlighting();
    };
    document.body.appendChild(script);
  };

  // Función para escapar HTML de forma segura
  const escapeHtml = (unsafe) => {
    return unsafe
      .replace(/&/g, "&amp;")
      .replace(/</g, "&lt;")
      .replace(/>/g, "&gt;")
      .replace(/"/g, "&quot;")
      .replace(/'/g, "&#039;");
  };

  // Manejar cambios en el texto
  const handleChange = (e) => {
    onChange(e);
  };

  // Manejar eventos de teclado (Tab, Enter, etc.)
  const handleKeyDown = (e) => {
    // Implementar tabulación personalizada con 2 espacios
    if (e.key === 'Tab') {
      e.preventDefault();
      
      const start = e.target.selectionStart;
      const end = e.target.selectionEnd;
      
      // Obtener el valor del textarea
      const value = e.target.value;
      
      // Insertar 2 espacios en la posición actual
      const newValue = value.substring(0, start) + '  ' + value.substring(end);
      
      // Establecer el nuevo valor
      e.target.value = newValue;
      
      // Mover el cursor después de los espacios insertados
      e.target.selectionStart = e.target.selectionEnd = start + 2;
      
      // Disparar evento de cambio
      const event = {
        target: {
          name: 'content',
          value: newValue
        }
      };
      handleChange(event);
    }
  };

  const styles = {
    container: {
      position: 'relative',
      width: '100%',
      height: '600px',
      fontSize: '14px',
      lineHeight: 1.5,
      overflow: 'hidden',
      borderRadius: '8px',
      border: '1px solid #e1e4e8'
    },
    highlighter: {
      position: 'absolute',
      top: 0,
      left: 0,
      width: '100%',
      height: '100%',
      padding: '16px',
      backgroundColor: '#272822', // Fondo estilo Monokai
      color: '#F8F8F2', // Color base de texto estilo Monokai
      fontFamily: "'Cascadia Code', 'Consolas', 'Monaco', 'Courier New', monospace",
      fontSize: '14px',
      lineHeight: 1.5,
      overflow: 'auto',
      whiteSpace: 'pre',
      pointerEvents: 'none',
      zIndex: 1
    },
    textarea: {
      position: 'absolute',
      top: 0,
      left: 0,
      width: '100%',
      height: '100%',
      padding: '16px',
      color: 'transparent',
      caretColor: '#F8F8F2', // Color de cursor claro para fondo oscuro
      backgroundColor: 'transparent',
      fontFamily: "'Cascadia Code', 'Consolas', 'Monaco', 'Courier New', monospace",
      fontSize: '14px',
      lineHeight: 1.5,
      border: 'none',
      resize: 'none',
      whiteSpace: 'pre',
      overflow: 'auto',
      outline: 'none',
      zIndex: 2
    }
  };

  return (
    <div ref={containerRef} style={styles.container}>
      {/* Estilos personalizados para mejorar la visualización */}
      <style dangerouslySetInnerHTML={{ __html: `
        /* Estilos para las líneas de código */
        .code-line {
          position: relative;
          min-height: 1.5em;
        }
        
        /* Estilos para los números de línea */
        .code-line::before {
          content: attr(data-line);
          position: absolute;
          left: -50px;
          width: 40px;
          color: #90908A;
          text-align: right;
          user-select: none;
          opacity: 0.8;
        }
        
        /* Ajustes para highlight.js */
        .hljs {
          background: #272822 !important;
          padding: 0 !important;
        }
      ` }} />
      
      {/* Capa de resaltado (solo visual) */}
      <div 
        ref={highlighterRef}
        style={styles.highlighter}
      />
      
      {/* Textarea para edición (visible pero con texto transparente) */}
      <textarea
        ref={textAreaRef}
        value={content}
        onChange={handleChange}
        onKeyDown={handleKeyDown}
        onScroll={syncScroll}
        style={styles.textarea}
        spellCheck="false"
        placeholder="Escribe código HTML aquí..."
      />
    </div>
  );
};

export default SyntaxHighlighter;
