// src/components/admin/SyntaxHighlighter.jsx
// Versión simplificada para evitar cualquier tipo de inyección de código
import React, { useRef } from 'react';

const SyntaxHighlighter = ({ content, onChange, textAreaRef }) => {
  const containerRef = useRef(null);
  
  // Manejar cambios en el texto de manera simple
  const handleChange = (e) => {
    onChange(e);
  };

  // Manejar eventos de teclado para la tabulación
  const handleKeyDown = (e) => {
    // Implementar tabulación personalizada con 2 espacios
    if (e.key === 'Tab') {
      e.preventDefault();
      
      const start = e.target.selectionStart;
      const end = e.target.selectionEnd;
      
      // Obtener el valor del textarea
      const value = e.target.value;
      
      // Insertar 2 espacios en la posición actual
      const newValue = value.substring(0, start) + '  ' + value.substring(end);
      
      // Establecer el nuevo valor
      e.target.value = newValue;
      
      // Mover el cursor después de los espacios insertados
      e.target.selectionStart = e.target.selectionEnd = start + 2;
      
      // Disparar evento de cambio
      const event = {
        target: {
          name: 'content',
          value: newValue
        }
      };
      handleChange(event);
    }
  };

  const styles = {
    container: {
      position: 'relative',
      width: '100%',
      height: '600px',
      fontSize: '14px',
      lineHeight: 1.5,
      overflow: 'hidden',
      borderRadius: '8px',
      border: '1px solid #e1e4e8'
    },
    textarea: {
      width: '100%',
      height: '100%',
      padding: '16px',
      color: '#f8f8f2',
      backgroundColor: '#272822', // Fondo estilo Monokai
      fontFamily: "'Cascadia Code', 'Consolas', 'Monaco', 'Courier New', monospace",
      fontSize: '14px',
      lineHeight: 1.5,
      border: 'none',
      resize: 'none',
      whiteSpace: 'pre',
      overflow: 'auto',
      outline: 'none',
      caretColor: '#f8f8f2'
    }
  };

  return (
    <div ref={containerRef} style={styles.container}>
      {/* Editor simple sin resaltado - completamente seguro */}
      <textarea
        ref={textAreaRef}
        value={content}
        onChange={handleChange}
        onKeyDown={handleKeyDown}
        style={styles.textarea}
        spellCheck="false"
        placeholder="Escribe código HTML aquí..."
      />
    </div>
  );
};

export default SyntaxHighlighter;
