import React, { useState } from 'react';

function EduBlog() {
  const [activeCategory, setActiveCategory] = useState('Todos');
  
  const categories = [
    'Todos',
    'Noticias',
    'Técnicas de Estudio',
    'Problemáticas',
    'Educación de Calidad',
    'Herramientas',
    'Desarrollo Docente',
    'Comunidad'
  ];
  
  const featuredPost = {
    image: '/api/placeholder/600/350',
    category: 'desarrollo docente',
    time: '2 horas atrás',
    number: '01'
  };
  
  const posts = [
    {
      id: 1,
      title: 'Cómo Optimizar el Uso de Tecnología en el Aula',
      image: '/api/placeholder/150/100',
      category: 'herramientas',
      time: '4 horas atrás',
      number: '02'
    },
    {
      id: 2,
      title: 'Entendiendo la Psicología del Aprendizaje Visual',
      image: '/api/placeholder/150/100',
      category: 'técnicas de estudio',
      time: '4 horas atrás',
      number: '02'
    },
    {
      id: 3,
      title: 'El Poder de la Colaboración en Entornos Educativos',
      image: '/api/placeholder/150/100',
      category: 'comunidad',
      time: '4 horas atrás',
      number: '02'
    },
    {
      id: 4,
      title: 'Construyendo un Sistema Educativo Inclusivo',
      image: '/api/placeholder/150/100',
      category: 'educación de calidad',
      time: '4 horas atrás',
      number: '02'
    }
  ];
  
  return (
    <div className="bg-gray-100 min-h-screen">
      {/* Header */}
      <header className="bg-white shadow-sm">
        <div className="container mx-auto px-4 py-4 flex justify-between items-center">
          <a href="#" className="flex items-center text-blue-700 text-2xl font-bold">
            <div className="bg-blue-700 text-white w-7 h-7 flex items-center justify-center rounded mr-2">E</div>
            edublog
          </a>
          
          <div className="hidden md:flex space-x-8">
            <a href="#" className="text-gray-800 font-medium">Inicio</a>
            <a href="#" className="text-gray-800 font-medium">Tendencias</a>
            <a href="#" className="text-gray-800 font-medium">Popular</a>
            <a href="#" className="text-gray-800 font-medium">Acerca de</a>
          </div>
          
          <div className="w-9 h-9 bg-gray-200 rounded-full overflow-hidden">
            <img src="/api/placeholder/40/40" alt="Profile" className="w-full h-full object-cover" />
          </div>
        </div>
      </header>
      
      {/* Main Content */}
      <main className="container mx-auto px-4">
        {/* Breadcrumb */}
        <div className="text-gray-500 text-sm my-5">
          <a href="#" className="text-gray-500">Inicio</a> <span className="mx-1">•</span> Blogs y Artículos
        </div>
        
        {/* Hero Section */}
        <div className="my-10">
          <h1 className="text-4xl font-bold text-gray-800 mb-4 leading-tight">Tu Destino para Educación, Innovación y Crecimiento</h1>
          <p className="text-lg text-gray-600 mb-8">Descubre consejos, tendencias y técnicas para mejorar tu experiencia educativa y desarrollo profesional.</p>
          
          <div className="relative ml-auto w-20 h-20 bg-blue-700 rounded-full flex items-center justify-center text-white">
            <div className="absolute w-full h-full text-xs text-blue-200 animate-spin-slow">• Comparte tu historia • Comparte tu idea • Comparte tu visión •</div>
            <div className="text-2xl">→</div>
          </div>
        </div>
        
        {/* Categories */}
        <div className="flex flex-wrap gap-2 bg-white p-4 rounded-full shadow mb-10">
          {categories.map(category => (
            <button
              key={category}
              className={`px-4 py-2 rounded-full text-sm transition-colors ${
                activeCategory === category ? 'bg-blue-700 text-white' : 'text-gray-800 hover:bg-gray-100'
              }`}
              onClick={() => setActiveCategory(category)}
            >
              {category}
            </button>
          ))}
          
          <div className="relative flex-1 max-w-xs ml-auto">
            <span className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-500">🔍</span>
            <input
              type="text"
              placeholder="Buscar un artículo..."
              className="w-full py-2 pl-8 pr-4 bg-gray-100 rounded-full focus:outline-none"
            />
          </div>
        </div>
        
        {/* Featured Post and Grid */}
        <div className="flex flex-col md:flex-row gap-8 mb-10">
          {/* Featured Post */}
          <div className="md:w-3/5 relative">
            <img
              src={featuredPost.image}
              alt="Featured Post"
              className="w-full h-80 object-cover rounded-lg"
            />
            <div className="absolute left-4 bottom-4 bg-white bg-opacity-80 px-4 py-1 rounded-full text-sm font-bold">
              <span className="text-gray-500 mr-1">{featuredPost.number}.</span> {featuredPost.category}
            </div>
            <div className="absolute left-40 bottom-4 bg-white bg-opacity-80 px-4 py-1 rounded-full text-sm text-blue-700">
              {featuredPost.time}
            </div>
          </div>
          
          {/* Posts Grid */}
          <div className="md:w-2/5 grid grid-cols-1 gap-6">
            {posts.map(post => (
              <div key={post.id} className="flex gap-4">
                <div className="w-36 h-24">
                  <img
                    src={post.image}
                    alt={post.title}
                    className="w-full h-full object-cover rounded-lg"
                  />
                </div>
                <div className="flex-1">
                  <div className="flex items-center gap-3 mb-2">
                    <div className="text-sm font-bold text-gray-800">{post.number}.</div>
                    <div className="text-sm font-medium text-blue-700">{post.category}</div>
                    <div className="text-sm text-gray-500">{post.time}</div>
                  </div>
                  <h3 className="text-lg font-semibold text-gray-800">{post.title}</h3>
                </div>
              </div>
            ))}
          </div>
        </div>
      </main>
    </div>
  );
}

// Add this to your CSS or in a style tag to handle the animation
const styles = `
@keyframes spin-slow {
  from { transform: rotate(0deg); }
  to { transform: rotate(360deg); }
}
.animate-spin-slow {
  animation: spin-slow 20s linear infinite;
}
`;

export default EduBlog;
