  content: counter(line);
  counter-increment: line;
  position: absolute;
  left: -50px;
  top: 0;
  width: 40px;
  color: #636d83;
  text-align: right;
  user-select: none;
  opacity: 0.5;
}

/* Líneas vacías */
.syntax-highlight-editor div:empty {
  min-height: 1.5em;
}

.syntax-highlight-editor div:empty:after {
  content: " ";
}

