import React, { useState, useEffect } from 'react';
import { colors, spacing, typography, shadows, borderRadius } from '../../styles/theme';

// Componentes para el editor
import DualModeEditor from './DualModeEditor';
import PostMetadata from './PostMetadata';
import CoverImageUploader from './CoverImageUploader';
import StatusMessage from './StatusMessage';
import ImportExportActions from './ImportExportActions';

// Funciones para el almacenamiento local
import { savePostToLocalStorage, loadPostFromLocalStorage } from './utils/storageUtils';

const PostEditor = () => {
  // Define los estilos aqu√≠ - el error com√∫n es que 'styles' no est√° definido
  const styles = {
    container: {
      maxWidth: "1200px",
      margin: "0 auto",
      padding: `${spacing.xl} ${spacing.md}`,
      fontFamily: typography.fontFamily
    },
    editorContainer: {
      display: "grid",
      gridTemplateColumns: "320px 1fr",
      gap: spacing.xl,
      '@media (max-width: A768px)': {
        gridTemplateColumns: "1fr"
      }
    },
    sidebar: {
      '@media (max-width: 768px)': {
        order: 2
      }
    },
    mainEditor: {
      '@media (max-width: 768px)': {
        order: 1
      }
    },
    formGroup: {
      marginBottom: spacing.lg
    },
    actionsContainer: {
      display: "flex",
      justifyContent: "flex-end",
      gap: spacing.md,
      marginTop: spacing.xl
    },
    actionButton: {
      padding: `${spacing.md} ${spacing.xl}`,
      borderRadius: borderRadius.md,
      border: "none",
      fontWeight: typography.fontWeight.medium,
      fontSize: typography.fontSize.md,
      cursor: "pointer",
      transition: "all 0.3s ease"
    },
    saveButton: {
      backgroundColor: colors.secondary,
      color: colors.primary,
      boxShadow: shadows.sm,
      '&:hover': {
        backgroundColor: colors.white,
        boxShadow: shadows.md
      }
    },
    publishButton: {
      backgroundColor: colors.primary,
      color: colors.white,
      boxShadow: shadows.sm,
      '&:hover': {
        backgroundColor: colors.primaryLight,
        boxShadow: shadows.md
      }
    }
  };

  const [post, setPost] = useState({
    title: '',
    category: '',
    content: '', 
    tags: '',
    coverImage: null,
    coverImagePreview: null,
    status: 'draft', 
    publishDate: new Date().toISOString().slice(0, 10),
    editorMode: 'simple', // Modo predeterminado es simple
  });

  // Estado para el mensaje de guardado
  const [saveMessage, setSaveMessage] = useState(null);
  
  // Estado para los indicadores de carga
  const [isSaving, setIsSaving] = useState(false);
  const [isPublishing, setIsPublishing] = useState(false);

  // Categor√≠as disponibles para el selector
  const categories = [
    'Noticias',
    'T√©cnicas de Estudio',
    'Problem√°ticas',
    'Educaci√≥n de Calidad',
    'Herramientas',
    'Desarrollo Docente',
    'Comunidad'
  ];

  // Cargar el borrador guardado al iniciar el componente
  useEffect(() => {
    const savedPost = loadPostFromLocalStorage();
    if (savedPost) {
      setPost(prevPost => ({
        ...prevPost,
        ...savedPost
      }));
    }
  }, []);

  // Manejar cambios en los campos del formulario
  const handleChange = (e) => {
    const { name, value } = e.target;
    
    setPost(prevPost => ({
      ...prevPost,
      [name]: value
    }));
    
    // Autoguardado despu√©s de cambios
    savePostToLocalStorage({
      ...post,
      [name]: value
    });
  };

  // Manejar cambios en la imagen de portada
  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    
    setPost(prevPost => ({
      ...prevPost,
      coverImage: file,
      coverImagePreview: URL.createObjectURL(file)
    }));
  };

  // Guardar el borrador
  const saveDraft = () => {
    setIsSaving(true);
    setSaveMessage(null);
    
    // Simular guardado (aqu√≠ ir√≠a una llamada real a API)
    setTimeout(() => {
      savePostToLocalStorage(post);
      
      setIsSaving(false);
      setSaveMessage({
        type: 'success',
        text: 'Borrador guardado correctamente',
        icon: 'üíæ'
      });
      
      setTimeout(() => setSaveMessage(null), 3000);
    }, 1000);
  };

  // Publicar el post
  const publishPost = () => {
    // Validaci√≥n b√°sica
    if (!post.title.trim() || !post.content.trim() || !post.category) {
      setSaveMessage({
        type: 'error',
        text: 'Por favor completa los campos obligatorios: t√≠tulo, contenido y categor√≠a',
        icon: '‚ö†Ô∏è'
      });
      return;
    }
    
    setIsPublishing(true);
    setSaveMessage(null);
    
    // Simular publicaci√≥n (aqu√≠ ir√≠a una llamada real a API)
    setTimeout(() => {
      setIsPublishing(false);
      setSaveMessage({
        type: 'success',
        text: 'Post publicado correctamente',
        icon: 'üöÄ'
      });
      
      // Reiniciar formulario despu√©s de publicar
      setPost({
        title: '',
        category: '',
        content: '', 
        tags: '',
        coverImage: null,
        coverImagePreview: null,
        status: 'draft',
        publishDate: new Date().toISOString().slice(0, 10),
        editorMode: 'simple'
      });
      
      // Limpiar localStorage
      localStorage.removeItem('post_draft');
      
      setTimeout(() => setSaveMessage(null), 3000);
    }, 1500);
  };

  // Modificar la funci√≥n exportToFile
  const exportToFile = () => {
    // Crear un objeto de texto para descargar
    let content = '';
    let fileExtension = 'html';
    
    content = post.content;
    
    const blob = new Blob([content], { 
      type: 'text/html' 
    });
    const url = URL.createObjectURL(blob);
    
    // Crear un enlace de descarga y hacer clic en √©l
    const a = document.createElement('a');
    a.href = url;
    a.download = `${post.title.replace(/[^a-z0-9]/gi, '-').toLowerCase()}.${fileExtension}`;
    document.body.appendChild(a);
    a.click();
    
    // Limpiar
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
    
    // Mostrar mensaje de √©xito
    setSaveMessage({
      type: 'success',
      text: `Archivo HTML descargado correctamente`,
      icon: 'üì•'
    });
    
    setTimeout(() => setSaveMessage(null), 3000);
  };

  // Modificar la funci√≥n importFile para manejar solo HTML
  const importFile = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    
    const reader = new FileReader();
    reader.onload = (event) => {
      const content = event.target.result;
      const fileExtension = file.name.split('.').pop().toLowerCase();
      
      // Verificar si es HTML
      const isHTML = fileExtension === 'html' || fileExtension === 'htm';
      
      if (isHTML) {
        // Extraer el t√≠tulo del documento HTML si existe
        const titleMatch = content.match(/<title>(.*?)<\/title>/i);
        const title = titleMatch ? titleMatch[1] : '';
        
        // Actualizar el estado con el contenido HTML
        setPost(prevPost => ({
          ...prevPost,
          title: title || prevPost.title,
          content: content,
          editorMode: 'html'
        }));
      } else {
        // Si no es HTML, tratar como texto plano
        setPost(prevPost => ({
          ...prevPost,
          content: content
        }));
      }
      
      // Mostrar mensaje de √©xito
      setSaveMessage({
        type: 'success',
        text: `Archivo ${isHTML ? 'HTML' : 'de texto'} importado correctamente`,
        icon: 'üì§'
      });
      
      setTimeout(() => setSaveMessage(null), 3000);
    };
    
    reader.readAsText(file);
  };

  return (
    <div style={styles.container}>
      <div style={styles.editorContainer}>
        {/* Sidebar - Ahora a la izquierda */}
        <div style={styles.sidebar}>
          <CoverImageUploader 
            coverImagePreview={post.coverImagePreview} 
            onChange={handleImageChange} 
          />

          <PostMetadata 
            post={post} 
            categories={categories} 
            onChange={handleChange} 
          />
          
          <ImportExportActions 
            onExport={exportToFile} 
            onImport={importFile}
          />
        </div>

        {/* Main Editor - Ahora a la derecha */}
        <div style={styles.mainEditor}>
          <div style={styles.formGroup}>
            <label style={{
              display: 'flex',
              alignItems: 'center',
              gap: spacing.xs,
              marginBottom: spacing.xs,
              fontWeight: typography.fontWeight.medium,
              color: colors.primary
            }} htmlFor="title">
              <span style={{color: colors.primary, fontSize: '1.1em'}}>üìù</span> T√≠tulo del post
            </label>
            <input
              type="text"
              id="title"
              name="title"
              value={post.title}
              onChange={handleChange}
              style={{
                width: "100%",
                padding: spacing.md,
                borderRadius: borderRadius.md,
                border: `1px solid ${colors.gray200}`,
                fontSize: typography.fontSize.lg,
                transition: "all 0.3s ease",
                marginBottom: spacing.md,
                fontWeight: typography.fontWeight.semiBold,
                borderLeft: `4px solid ${colors.primary}`
              }}
              placeholder="Escribe un t√≠tulo atractivo"
              onFocus={(e) => {
                e.target.style.boxShadow = `0 0 0 2px ${colors.primary}30`;
                e.target.style.borderLeft = `4px solid ${colors.secondary}`;
              }}
              onBlur={(e) => {
                e.target.style.boxShadow = 'none';
                e.target.style.borderLeft = `4px solid ${colors.primary}`;
              }}
            />
          </div>

          <div style={styles.formGroup}>
            <DualModeEditor 
              content={post.content}
              onChange={handleChange}
              initialMode={post.editorMode}
            />
          </div>

          {saveMessage && (
            <StatusMessage 
              type={saveMessage.type} 
              text={saveMessage.text} 
              icon={saveMessage.icon} 
            />
          )}

          <div style={styles.actionsContainer}>
            <button 
              onClick={saveDraft}
              disabled={isSaving}
              style={{
                ...styles.actionButton,
                ...styles.saveButton
              }}
            >
              {isSaving ? 'Guardando...' : 'Guardar borrador'}
            </button>
            
            <button 
              onClick={publishPost}
              disabled={isPublishing}
              style={{
                ...styles.actionButton,
                ...styles.publishButton
              }}
            >
              {isPublishing ? 'Publicando...' : 'Publicar post'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PostEditor;
